import React, { useState } from "react";

function ListaAnimais() {
  // Lista inicial de animais (antes de qualquer pet ser adicionado manualmente)
  const listaInicialDeAnimais = [
    {
      id: 1,
      nome: "Rex",
      descricao: "Cachorro amigável e brincalhão",
      idade: 3,
      imagem: "imagem_do_rex.jpg",
      status: "Disponível",
      local: false, // Marca como pet da API (não adicionado manualmente)
    },
    {
      id: 2,
      nome: "Miau",
      descricao: "Gato tranquilo e carinhoso",
      idade: 2,
      imagem: "imagem_do_miau.jpg",
      status: "Disponível",
      local: false, // Marca como pet da API
    },
    // Adicione mais animais se necessário
  ];

  const [animais, setAnimais] = useState(listaInicialDeAnimais); // Inicializa os animais com a lista inicial

  // Função para resetar a lista de animais para o estado inicial
  const resetarLista = () => {
    // Limpar o LocalStorage
    localStorage.removeItem("animais");

    // Resetando o estado para a lista inicial de animais
    setAnimais(listaInicialDeAnimais); // Atualiza o estado
    localStorage.setItem("animais", JSON.stringify(listaInicialDeAnimais)); // Atualiza o LocalStorage com a lista inicial

    console.log("Lista de animais foi resetada.");
  };

  // Função para adotar um animal
  const adotarAnimal = (id) => {
    const animalAdotado = animais.find((animal) => animal.id === id);
    animalAdotado.status = "Adotado"; // Marca como adotado

    // Atualiza o estado e o LocalStorage
    const novosAnimais = [...animais];
    const index = novosAnimais.findIndex((animal) => animal.id === id); // Encontra o índice do animal
    novosAnimais[index] = animalAdotado;

    setAnimais(novosAnimais);
    localStorage.setItem("animais", JSON.stringify(novosAnimais));

    alert(`Parabéns! Você adotou o pet: ${animalAdotado.nome}`);
  };

  return (
    <div>
      <h2>Lista de Animais para Adoção</h2>

      {/* Botão para resetar a lista */}
      <button onClick={resetarLista}>Resetar Lista Inicial</button>

      {/* Aqui vai a exibição dos animais */}
      {animais.length > 0 ? (
        <ul className="listagem">
          {animais.map((animal) => (
            <li key={animal.id}>
              <img
                src={animal.imagem || "default-image.jpg"} // Verifica se a imagem existe, senão usa uma padrão
                alt={animal.nome}
                style={{ width: "100px", height: "100px", objectFit: "cover" }}
              />
              <div>
                <h3>{animal.nome}</h3>
                <p>{animal.descricao}</p>
                <p>
                  <strong>Idade:</strong> {animal.idade} anos
                </p>
                {animal.status === "Adotado" ? (
                  <p style={{ color: "green" }}>Este pet foi adotado!</p>
                ) : (
                  <button
                    onClick={() => adotarAnimal(animal.id)}
                    className="btn-adotar"
                  >
                    Adotar Pet
                  </button>
                )}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum animal disponível para adoção.</p>
      )}
    </div>
  );
}

export default ListaAnimais;
