import React, { useState, useEffect } from "react";

function ListaAnimais() {
  const [animais, setAnimais] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulando uma requisição à API externa
    fetch("https://api.example.com/animais") // Substitua com a URL real da API
      .then((response) => response.json())
      .then((data) => {
        setAnimais(data); // Preenche a lista com os dados obtidos
        setLoading(false); // Finaliza o loading
      })
      .catch((error) => {
        console.error("Erro ao buscar os dados:", error);
        setLoading(false);
      });
  }, []); // O array vazio significa que esse efeito será chamado uma vez após o primeiro render

  return (
    <div>
      <h2>Lista de Animais para Adoção</h2>
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <ul className="listagem">
          {animais.length > 0 ? (
            animais.map((animal, index) => (
              <li key={index}>
                <h3>{animal.nome}</h3>
                <p>{animal.descricao}</p>
                <p>
                  <strong>Idade:</strong> {animal.idade} anos
                </p>
              </li>
            ))
          ) : (
            <p>Nenhum animal disponível para adoção.</p>
          )}
        </ul>
      )}
    </div>
  );
}

export default ListaAnimais;
