import React from "react";

function ListaAnimais({ animais, setAnimais }) {
  const removerAnimal = (id) => {
    console.log("Tentando remover animal com id:", id);

    const animalToRemove = animais.find((animal) => animal.id === id);

    if (!animalToRemove) {
      console.log("Animal não encontrado para remoção");
      return;
    }

    // Exemplo de um DELETE para remover o animal da API
    fetch(`https://sua-api.com/animais/${id}`, {
      // Usando o ID diretamente
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Falha ao remover o animal da API");
        }
        return response.json();
      })
      .then(() => {
        console.log("Animal removido da API:", animalToRemove);

        // Remover o animal da lista de estado
        const novosAnimais = animais.filter((animal) => animal.id !== id);
        setAnimais(novosAnimais);

        // Remover o animal do LocalStorage
        localStorage.setItem("animais", JSON.stringify(novosAnimais));

        console.log("Lista de animais após remoção:", novosAnimais);
      })
      .catch((error) => {
        console.error("Erro ao remover o animal:", error);
      });
  };

  const adotarAnimal = (id) => {
    const animalAdotado = animais.find((animal) => animal.id === id); // Encontrar o animal pelo ID
    animalAdotado.status = "Adotado"; // Marca como adotado

    // Atualiza o estado e o LocalStorage
    const novosAnimais = [...animais];
    const index = novosAnimais.findIndex((animal) => animal.id === id); // Encontra o índice do animal
    novosAnimais[index] = animalAdotado;

    setAnimais(novosAnimais);
    localStorage.setItem("animais", JSON.stringify(novosAnimais));

    alert(`Parabéns! Você adotou o pet: ${animalAdotado.nome}`);
  };

  return (
    <div>
      <h2>Lista de Animais para Adoção</h2>
      {animais.length > 0 ? (
        <ul className="listagem">
          {animais.map((animal) => (
            <li key={animal.id}>
              <img
                src={animal.imagem || "default-image.jpg"} // Verifica se a imagem existe, senão usa uma padrão
                alt={animal.nome}
                style={{ width: "100px", height: "100px", objectFit: "cover" }}
              />
              <div>
                <h3>{animal.nome}</h3>
                <p>{animal.descricao}</p>
                <p>
                  <strong>Idade:</strong> {animal.idade} anos
                </p>
                {animal.status === "Adotado" ? (
                  <p style={{ color: "green" }}>Este pet foi adotado!</p>
                ) : (
                  <button
                    onClick={() => adotarAnimal(animal.id)}
                    className="btn-adotar"
                  >
                    Adotar Pet
                  </button>
                )}
                {/* O botão Remover Pet sempre será exibido */}
                <button
                  onClick={() => removerAnimal(animal.id)} // Passando o ID para remover
                  className="btn-remover"
                >
                  Remover Pet
                </button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum animal disponível para adoção.</p>
      )}
    </div>
  );
}

export default ListaAnimais;
