import React, { useState } from "react";

function Cadastro({ setAnimais }) {
  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [idade, setIdade] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Criação de um novo animal com os dados do formulário
    const novoAnimal = { nome, descricao, idade };

    // Atualizar LocalStorage com o novo animal
    let animais = JSON.parse(localStorage.getItem("animais")) || [];
    animais.push(novoAnimal);
    localStorage.setItem("animais", JSON.stringify(animais));

    // Atualizar a lista de animais no estado, passando para o componente pai
    setAnimais((prevAnimais) => [...prevAnimais, novoAnimal]);

    // Limpar o formulário após o envio
    setNome("");
    setDescricao("");
    setIdade("");
    alert("Animal cadastrado com sucesso!");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome do Animal:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            placeholder="Digite o nome do animal"
          />
        </div>

        <div className="form-group">
          <label>Descrição:</label>
          <textarea
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
            placeholder="Descreva o animal"
          />
        </div>

        <div className="form-group">
          <label>Idade:</label>
          <input
            type="number"
            value={idade}
            onChange={(e) => setIdade(e.target.value)}
            placeholder="Idade do animal"
          />
        </div>

        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default Cadastro;
