import React, { useState, useEffect } from "react";

function ListaAnimais() {
  const [animais, setAnimais] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Requisição à API de exemplo fictícia
    fetch("https://jsonplaceholder.typicode.com/users") // URL da API de exemplo
      .then((response) => response.json())
      .then((data) => {
        // Simulando os dados de animais com base na estrutura da API
        const animaisData = data.map((user, index) => ({
          nome: getNomeAnimal(index), // Nome do animal baseado em um array de nomes
          descricao: `Este é o animal ${getNomeAnimal(
            index
          )}, ele é amigável e adora brincar.`, // Descrição fictícia
          idade: Math.floor(Math.random() * 6) + 1, // Idade aleatória entre 1 e 6 anos
        }));
        setAnimais(animaisData); // Atualiza a lista com os dados mapeados
        setLoading(false); // Finaliza o loading
      })
      .catch((error) => {
        console.error("Erro ao buscar os dados:", error);
        setLoading(false);
      });
  }, []); // O array vazio significa que esse efeito será chamado uma vez após o primeiro render

  // Função para pegar um nome fictício de animal
  const getNomeAnimal = (index) => {
    const nomes = [
      "Rex",
      "Miau",
      "Fido",
      "Luna",
      "Max",
      "Bella",
      "Rocky",
      "Oliver",
      "Charlie",
      "Lily",
      "Simba",
      "Nala",
      "Coco",
      "Daisy",
      "Buddy",
      "Ziggy",
      "Chester",
      "Maggie",
      "Ruby",
      "Jack",
    ];
    return nomes[index % nomes.length]; // Garantir que não saia do array de nomes
  };

  return (
    <div>
      <h2>Lista de Animais para Adoção</h2>
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <ul className="listagem">
          {animais.length > 0 ? (
            animais.map((animal, index) => (
              <li key={index}>
                <h3>{animal.nome}</h3>
                <p>{animal.descricao}</p>
                <p>
                  <strong>Idade:</strong> {animal.idade} anos
                </p>
              </li>
            ))
          ) : (
            <p>Nenhum animal disponível para adoção.</p>
          )}
        </ul>
      )}
    </div>
  );
}

export default ListaAnimais;
