import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Cadastro({ setAnimais }) {
  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [idade, setIdade] = useState("");
  const [imagem, setImagem] = useState(null); // Para armazenar a imagem
  const navigate = useNavigate();

  const handleImageChange = (e) => {
    const file = e.target.files[0]; // Pega o arquivo de imagem selecionado
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagem(reader.result); // Armazena a imagem no estado como uma URL base64
      };
      reader.readAsDataURL(file); // Lê o arquivo como uma URL base64
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Criação de um novo animal com os dados do formulário
    const novoAnimal = { nome, descricao, idade, imagem };

    // Atualizar LocalStorage com o novo animal
    let animais = JSON.parse(localStorage.getItem("animais")) || [];
    animais.push(novoAnimal);
    localStorage.setItem("animais", JSON.stringify(animais));

    // Atualizar a lista de animais no estado, passando para o componente pai
    setAnimais((prevAnimais) => [...prevAnimais, novoAnimal]);

    // Limpar o formulário após o envio
    setNome("");
    setDescricao("");
    setIdade("");
    setImagem(null); // Limpar a imagem

    // Navegar para a página principal após cadastro
    navigate("/");
    alert("Animal cadastrado com sucesso!");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome do Animal:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            placeholder="Digite o nome do animal"
          />
        </div>

        <div className="form-group">
          <label>Descrição:</label>
          <textarea
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
            placeholder="Descreva o animal"
          />
        </div>

        <div className="form-group">
          <label>Idade:</label>
          <input
            type="number"
            value={idade}
            onChange={(e) => setIdade(e.target.value)}
            placeholder="Idade do animal"
          />
        </div>

        <div className="form-group">
          <label>Imagem do Animal:</label>
          <input
            type="file"
            onChange={handleImageChange} // A função que lida com a imagem
            accept="image/*" // Limita a seleção a arquivos de imagem
          />
        </div>

        {imagem && (
          <img
            src={imagem}
            alt="Imagem do animal"
            style={{ width: "100px", marginTop: "10px" }}
          />
        )}

        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default Cadastro;
